#!/bin/bash
declare -A models
models["huggingface"]="AlbertForMaskedLM	AlbertForQuestionAnswering	AllenaiLongformerBase	BartForCausalLM	BartForConditionalGeneration	BertForMaskedLM	BertForQuestionAnswering	BlenderbotForCausalLM	BlenderbotSmallForCausalLM	BlenderbotSmallForConditionalGeneration	CamemBert	DebertaForMaskedLM	DebertaForQuestionAnswering	DebertaV2ForMaskedLM	DebertaV2ForQuestionAnswering	DistilBertForMaskedLM	DistilBertForQuestionAnswering	DistillGPT2	ElectraForCausalLM	ElectraForQuestionAnswering	GPT2ForSequenceClassification	GoogleFnet	LayoutLMForMaskedLM	LayoutLMForSequenceClassification	M2M100ForConditionalGeneration	MBartForCausalLM	MBartForConditionalGeneration	MT5ForConditionalGeneration	MegatronBertForCausalLM	MegatronBertForQuestionAnswering	MobileBertForMaskedLM	MobileBertForQuestionAnswering	OPTForCausalLM	PLBartForCausalLM	PLBartForConditionalGeneration	PegasusForCausalLM	PegasusForConditionalGeneration	RobertaForCausalLM	RobertaForQuestionAnswering	Speech2Text2ForCausalLM	T5ForConditionalGeneration	T5Small	TrOCRForCausalLM	XGLMForCausalLM	XLNetLMHeadModel	YituTechConvBert"	
models["timm_models"]="adv_inception_v3	beit_base_patch16_224	botnet26t_256	cait_m36_384	coat_lite_mini	convit_base	convmixer_768_32	convnext_base	crossvit_9_240	cspdarknet53	deit_base_distilled_patch16_224	dla102	dm_nfnet_f0	dpn107	eca_botnext26ts_256	eca_halonext26ts	ese_vovnet19b_dw	fbnetc_100	fbnetv3_b	gernet_l	ghostnet_100	gluon_inception_v3	gluon_xception65	gmixer_24_224	gmlp_s16_224	hrnet_w18	inception_v3	jx_nest_base	lcnet_050	levit_128	mixer_b16_224	mixnet_l	mnasnet_100	mobilenetv2_100	mobilenetv3_large_100	mobilevit_s	nfnet_l0	pit_b_224	pnasnet5large	poolformer_m36	regnety_002	repvgg_a2	res2net101_26w_4s	res2net50_14w_8s	res2next50	resmlp_12_224	resnest101e	rexnet_100	sebotnet33ts_256	selecsls42b	spnasnet_100	swin_base_patch4_window7_224	swsl_resnext101_32x16d	tf_efficientnet_b0	tf_mixnet_l	tinynet_a	tnt_s_patch16_224	twins_pcpvt_base	visformer_small	vit_base_patch16_224	volo_d1_224	xcit_large_24_p8_224"	
models["torchbench"]="BERT_pytorch	Background_Matting	LearningToPaint	Super_SloMo	alexnet	dcgan	demucs	densenet121	detectron2_fasterrcnn_r_101_c4	detectron2_fasterrcnn_r_101_dc5	detectron2_fasterrcnn_r_101_fpn	detectron2_fasterrcnn_r_50_c4	detectron2_fasterrcnn_r_50_dc5	detectron2_fasterrcnn_r_50_fpn	detectron2_fcos_r_50_fpn	detectron2_maskrcnn	detectron2_maskrcnn_r_101_c4	detectron2_maskrcnn_r_101_fpn	detectron2_maskrcnn_r_50_c4	detectron2_maskrcnn_r_50_fpn	dlrm	drq	fastNLP_Bert	hf_Albert	hf_Bart	hf_Bert	hf_BigBird	hf_DistilBert	hf_GPT2	hf_Longformer	hf_T5	maml	maml_omniglot	mnasnet1_0	mobilenet_v2	mobilenet_v3_large	moco	nvidia_deeprecommender	opacus_cifar10	pyhpc_equation_of_state	pyhpc_isoneutral_mixing	pyhpc_turbulent_kinetic_energy	pytorch_CycleGAN_and_pix2pix	pytorch_stargan	pytorch_unet	resnet18	resnet50	resnext50_32x4d	shufflenet_v2_x1_0	soft_actor_critic	speech_transformer	squeezenet1_1	tacotron2	timm_efficientnet	timm_nfnet	timm_regnet	timm_resnest	timm_vision_transformer	timm_vovnet	tts_angular	vgg16	vision_maskrcnn"

#script_name="huggingface"
#script_name="timm_models"
script_name="torchbench"
orig_dir=`pwd`
device=cuda
precision=float32
for model in ${models[$script_name]}; do
  echo "Processing : $model"
  model_dir="models/$model"
  mkdir -p "$model_dir"
  #cd $model_dir
  GPU_NUM_DEVICES=1 python $orig_dir/benchmarks/dynamo/${script_name}.py --${precision} -d${device} --output-directory=./ --no-skip --dashboard --batch_size 1 --tolerance --dump-fx --only $model "$@"
  if test -d "fx_dump"; then
    mv fx_dump $model_dir
  fi  
  cd $orig_dir
done
